-- Create a function to get exam statistics
CREATE OR REPLACE FUNCTION get_exam_statistics()
RETURNS TABLE (
    total_current_exams BIGINT,
    total_historic_exams BIGINT,
    total_courses BIGINT,
    total_buildings BIGINT
) AS $$
BEGIN
    RETURN QUERY
    SELECT 
        (SELECT COUNT(*) FROM current_exams) as total_current_exams,
        (SELECT COUNT(*) FROM historic_exams) as total_historic_exams,
        (SELECT COUNT(*) FROM course_catalog) as total_courses,
        (SELECT COUNT(*) FROM buildings) as total_buildings;
END;
$$ LANGUAGE plpgsql; 